import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [loading, setLoading] = useState(false);  // Loading state
  const [error, setError] = useState(null);  // Error state
  const [message, setMessage] = useState('');
  const [isEditing, setIsEditing] = useState(false);  // Editing state
  const [editTaskId, setEditTaskId] = useState(null);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    fetchTasks();
  }, []);

  const startEditing = (task) => {
    setIsEditing(true);
    setEditTaskId(task.id);
    setNewTask(task.title);  // Populate input with the task title
  };

  const fetchTasks = async () => {
    setLoading(true);  // Set loading to true
    try {
      const response = await axios.get('http://localhost:8000/api/tasks/');
      setTasks(response.data);
      setError(null);  // Clear error
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setError('Failed to fetch tasks.');  // Set error message
    } finally {
      setLoading(false);  // Set loading to false
    }
  };

  const addTask = async () => {
    setLoading(true);
    try {
      if (isEditing) {
        await axios.patch(`http://localhost:8000/api/tasks/${editTaskId}/`, { title: newTask });
        setMessage('Task updated successfully!');
      } else {
        await axios.post('http://localhost:8000/api/tasks/', { title: newTask });
        setMessage('Task added successfully!');
      }
      setNewTask('');
      setIsEditing(false);  // Reset editing state
      fetchTasks();
      setError(null);
    } catch (error) {
      console.error('Error adding/updating task:', error);
      setError('Failed to save task.');
      setMessage(null);
    } finally {
      setLoading(false);
    }
  };

  const deleteTask = async (id) => {
    setLoading(true);
    try {
      await axios.delete(`http://localhost:8000/api/tasks/${id}/`);
      fetchTasks();
      setMessage('Task deleted successfully!');
      setError(null);
    } catch (error) {
      console.error('Error deleting task:', error);
      setError('Failed to delete task.');
      setMessage(null);
    } finally {
      setLoading(false);
    }
  };

  const toggleTaskCompletion = async (task) => {
    const updatedTask = { ...task, completed: !task.completed };
    try {
      await axios.patch(`http://localhost:8000/api/tasks/${task.id}/`, updatedTask);
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
      setError('Failed to update task.');
    }
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === 'completed') return task.completed;
    if (filter === 'incomplete') return !task.completed;
    return true;  
  });

  return (
    <div className="App">
      <h1>Task Manager</h1>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {message && <p className="message">{message}</p>}
      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add new task"
      />
      <button onClick={addTask}>Add Task</button>
      <ul>
        {tasks.map((task) => (
          <li key={task.id} className={task.completed ? 'completed' : ''}>
            <span onClick={() => toggleTaskCompletion(task)}>{task.title}</span>
            <button onClick={() => startEditing(task)}>Edit</button>
            <button onClick={() => deleteTask(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
